      liquid:
        "{% render 'section-settings' %}\n\n{% capture Content %}\n  <div\n    class=\"row row-cols-lg-{{ responsive.content_item.lg }} row-cols-md-{{ responsive.content_item.md }} row-cols-sm-{{ responsive.content_item.sm }} row-cols-{{ responsive.content_item.xs }}\"\n    {% if section_settings.column_gap or section_settings.column_gap == 0 %}\n      style=\"{{ rowGap }}\"\n    {% endif %}\n  >\n    {% for item in content %}\n      {% assign aspectRatio = 100 %}\n      {% if item.aspect_ratio %}\n        {% assign aspectRatio = item.aspect_ratio %}\n      {% endif %}\n      {% if item.image_border_radius %}\n        {% assign imageRadius = 'border-radius:' | append: item.image_border_radius | append: 'px;' %}\n      {% else %}\n        {% assign imageRadius = '' %}\n      {% endif %}\n      <div\n        class=\"col\"\n        {% if section_settings.column_gap or section_settings.column_gap == 0 %}\n          style=\"{{ colGap }}\"\n        {% endif %}\n      >\n        <a\n          class=\"veda-image-cover pos:relative d:block w:100% ov:hidden\"\n          href=\"{{ item.link }}\"\n          style=\"{{ imageRadius }}--aspect-ratio: {{ aspectRatio }}\"\n          component=\"content\"\n        >\n          {% render 'image', _src: item.image.src, _width: item.image.width, _height: item.image.height %}\n\n          {% case item.position %}\n            {% when 'top_left' %}\n              {% assign pos = 't:10% l:10%' %}\n            {% when 'top_center' %}\n              {% assign pos = 't:10% l:50% trf:translateX(-50%)' %}\n            {% when 'top_right' %}\n              {% assign pos = 't:10% r:10%' %}\n            {% when 'center_right' %}\n              {% assign pos = 't:50% r:10% trf:translateY(-50%)' %}\n            {% when 'bottom_right' %}\n              {% assign pos = 'b:10% r:10%' %}\n            {% when 'bottom_center' %}\n              {% assign pos = 'b:10% l:50% trf:translateX(-50%)' %}\n            {% when 'bottom_left' %}\n              {% assign pos = 'b:10% l:10%' %}\n            {% when 'center_left' %}\n              {% assign pos = 't:50% l:10% trf:translatey(-50%)' %}\n            {% else %}\n              {% assign pos = 't:50% l:50% trf:translate(-50%,-50%) ta:center' %}\n          {% endcase %}\n\n          <h3\n            class=\"pos:absolute bgc:color-dark-freeze fw:500 fz:pfs(26px,45px) pt:pfs(3px,10px) pb:pfs(3px,10px) pl:pfs(10px,20px) pr:pfs(10px,20px) c:color-light-freeze tt:uppercase z:2 m:0 {{ pos }}\"\n            css=\"{{ item.title_design }}\"\n          >\n            {{ item.title }}\n          </h3>\n        </a>\n      </div>\n    {% endfor %}\n  </div>\n{% endcapture %}\n\n<section\n  data-id=\"{{ uniqueId }}\"\n  class=\"veda-lazyload pos:relative z:1 bgz:cover bgp:center\"\n  style=\"{{ backgroundColor }}{{ backgroundImage }}{{ sectionPadding }}\"\n>\n  <div\n    class=\"{{ containerType }}\"\n    {% if section_settings.container_gap\n      or section_settings.container_gap == 0\n      or section_settings.width\n      or section_settings.width == 0\n    %}\n      style=\"{{ containerGap }}{{ containerWidth }}\"\n    {% endif %}\n  >\n    <div\n      {% if responsive.heading_position == 'left' %}\n        class=\"row mt:0!\"\n      {% else %}\n        class=\"row mt:0! fld:row-reverse\"\n      {% endif %}\n    >\n      <div class=\"col-xs-{{ responsive.heading_and_content.xs }} col-sm-{{ responsive.heading_and_content.sm }} col-md-{{ responsive.heading_and_content.md }} col-lg-{{ responsive.heading_and_content.lg }} mt:0!\">\n        <div\n          {% if responsive.heading_and_content.lg == 12 %}\n            class=\"pos:relative d:flex ai:center jc:space-between\"\n          {% else %}\n            class=\"pos:relative\"\n          {% endif %}\n        >\n          {% render 'section-heading', _heading: heading %}\n          {% render 'button-view-all', _placement: 'top', _options: button_view_all %}\n        </div>\n      </div>\n      {% liquid\n        assign contentXs = 12 | minus: responsive.heading_and_content.xs\n        assign contentSm = 12 | minus: responsive.heading_and_content.sm\n        assign contentMd = 12 | minus: responsive.heading_and_content.md\n        assign contentLg = 12 | minus: responsive.heading_and_content.lg\n      %}\n      <div class=\"col-xs-{{ contentXs }} col-sm-{{ contentSm }} col-md-{{ contentMd }} col-lg-{{ contentLg }} mt:0!\">\n        {{ Content }}\n      </div>\n    </div>\n    {% render 'button-view-all', _placement: 'bottom', _options: button_view_all %}\n  </div>\n  {% render 'bg-video-and-overlay', _section_settings: section_settings %}\n</section>\n",
